import React from 'react';
import {connect} from 'react-redux';
import {set_curentPageAC, set_TotalCountAC, unfallowAC} from '../../redux/state/Users-reducer';
import {fallowAC, set_user} from './../../redux/state/Users-reducer';
import * as axios from "axios";

let mapStateToProps = (state) => {
    return {
        users: state.UsersData.users,
        pageSize: state.UsersData.pageSize,
        totalUsersCount: state.UsersData.totalUsersCount,
        curentPage: state.UsersData.curentPage
    }
};

let mapDispatchToProps = (dispatch) => {
    return {
        fallow: (userID) => {
            dispatch(fallowAC(userID));
        },
        unfallow: (userID) => {
            dispatch(unfallowAC(userID));
        },
        setuser: (users) => {
            dispatch(set_user(users));
        },
        setTotalCount:(TotalCount) =>{
            dispatch(set_TotalCountAC(TotalCount));
        },
        SetCurrentPage: (p) =>{
            dispatch(set_curentPageAC(p));
        }
    }
};
class Users extends React.Component{
    componentDidMount(){
        axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${this.props.currentPage}&cout=${this.props.pageSize}`).then(response => {
            this.props.setuser(response.data.items);
            this.props.setTotalCount(response.data.totalCount);
        });
    }
    onPageChanged = (p) => {
        this.props.SetCurrentPage(p);
        axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${p}&cout=${this.props.pageSize}`)
            .then(response => {
                this.props.setuser(response.data.items);
            });
    }
    render() {
        <Users />
    };
};
export default connect(mapStateToProps, mapDispatchToProps)(Users);
