let initialstate = {
    Users: [
      {id: 1, name: 'Artem', message: 'Hi, how are you?', follow: true, location:{city: 'Luvetpol', country: 'UK'} },
      {id: 1, name: 'Artem', message: 'Looking foe a job ?', follow: true, location:{city: 'Luvetpol', country: 'UK'} },
      {id: 1, name: 'Artem', message: 'Я СДАЛ СЕССИЮ', follow: true, location:{city: 'Luvetpol', country: 'UK'} },
      {id: 1, name: 'Artem', message: 'Насвай надо ?', follow: true, location:{city: 'Luvetpol', country: 'UK'} }
    ],
      newPostText : ''   
}
const UsersReducer = (state = initialstate, action) =>{
  
    if (action.type === 'UpdatePost'){
      return{
        ...state, newPostText : action.Newtext
      }
      
      }



      else if (action.type === 'AddPost'){
        let newPost = {
          id: 5,
          message: state.newPostText,
          likesCount: 0
        };

        return{
          ...state,
          Posts: [...state.Posts, newPost],
          newPostText : ' '
        };
      }
      else {
        return state;
      }
}

export const addPostcreator = (text) =>{
  return{
      type: 'AddPost', postMassage: text
  }
}

export const OnPostchangecreator = (text) =>{
  return{
      type: 'UpdatePost', Newtext: text
  }
}
export default UsersReducer;
