import React from 'react';
import Profile from './Profile';
import Loader from './loader';
import 'bootstrap/dist/css/bootstrap.min.css';
import classes from './Profile.module.css';
import * as axios from "axios";
import {connect} from 'react-redux';
import {getStatus, setProfile, setProfilePage} from "../../redux/state/PostsData-reducer";
import {Redirect, withRouter} from "react-router-dom";
import {withAuthRedirect} from "../hoc/withAuthRedirect";
import {compose} from "redux";
class ProfileContainer extends React.Component{
    componentDidMount()  {
        let userId = this.props.match.params.userId;
        if (!userId) {
            userId = 9;
        }
        this.props.setProfilePage(userId);
        this.props.getStatus(userId);
    }
    render() {

        return <>
            <Profile {...this.props}  profile={this.props.profile} status={this.props.status}/>
            </>
    }
}
let mapStateToProps = (state) =>({
    profile: state.PostsData.profile,
    status: state.PostsData.status
});



export default compose(
    connect(mapStateToProps, {setProfilePage, getStatus}),
    withRouter,
    withAuthRedirect
)(ProfileContainer);
