let initialstate = {
    Posts: [
      {id: 1, message: 'Hi, how are you?', likesCount: 12},
      {id: 2, message: 'It\'s my first post', likesCount: 11},
      {id: 3, message: 'Blabla', likesCount: 11},
      {id: 4, message: 'Dada', likesCount: 11} 
    ],
      newPostText : ''   
}
const PostsDataReducer = (state = initialstate, action) =>{
  
    if (action.type === 'UpdatePost'){
      return{
        ...state, newPostText : action.Newtext
      }
      
      }



      else if (action.type === 'AddPost'){
        return{
          ...state.Posts,

        }
        let newPost = {
          id: 5,
          message: state.newPostText,
          likesCount: 0
        };

        let Copystate = {...state};
        Copystate.Posts = [...state.Posts];
        Copystate.Posts.push(newPost);
        Copystate.newPostText = {...state.newPostText};
        Copystate.newPostText = ' ';
        return Copystate;
      }
      else {
        return state;
      }
}

export const addPostcreator = (text) =>{
  return{
      type: 'AddPost', postMassage: text
  }
}

export const OnPostchangecreator = (text) =>{
  return{
      type: 'UpdatePost', Newtext: text
  }
}
export default PostsDataReducer;
