import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import classes from './Users.module.css';
import {NavLink} from "react-router-dom";
import * as axios from "axios";
import {sucseccfallow, togglefollowingInProgress} from "../../redux/state/Users-reducer";
import Loader from "./loader";
import {ApiUnfollow, UserAPI} from "./Api";

const Users = (props) => {
    let pagesCount = Math.ceil(props.totalUsersCount / props.pageSize);
    let pages = [];
    for (let i = 1; i <= 20; i++) {
        pages.push(i);
    }
    return <div className={classes.user_card_wrap}>
        <div className={classes.spans}>
            {pages.map(p => {
                return <span className={props.curentPage === p && classes.SelectedPage}
                             onClick={() => {
                                 props.onPageChanged(p);
                             }}> {p} </span>
            })}
        </div>
        {
            props.users.map(u => <div key={u.id}>
            <span>
                <div>
                <div className={classes.user_card}>
                    <NavLink to={'/profile/' + u.id}>
                                            <img className={classes.profil_photo}
                                                 src={u.photos.small != null ? u.photos.small : "https://pbs.twimg.com/profile_images/1081531670656962561/J9QiHhLT_400x400.jpg"}/>
                    </NavLink>

                </div>
                <div>
                {u.follow
                    ? <button disabled={props.followingInProgress.some(id => id === u.id)} onClick={() => {
                        props.togglefollowingInProgress(true, u.id);
                        UserAPI.Unfollow(u.id)
                            .then(response => {
                                if (response.data.resultCode == 0){
                                    props.sucseccunfallow(u.id);
                                }
                                props.togglefollowingInProgress(false, u.id);
                            });


                    }}>Unfallow</button>
                    : <button disabled={props.followingInProgress.some(id => id === u.id)}  onClick={() => {
                        props.togglefollowingInProgress(true, u.id);
                        UserAPI.Follow(u.id)
                        .then(response => {
                            if (response.data.resultCode === 0){
                                props.sucseccfallow(u.id);
                            }
                            props.togglefollowingInProgress(false, u.id);
                        });


                    }}>Follow</button>
                }
                    <span>
                <span>
                     <div>{u.name}</div>
                </span>
                <span>
                     <div>
                     {"u.location.city"}
                     </div>
                     <div>
                     {"u.location.country"}
                     </div>
                </span>
            </span>
                </div>
                </div>
            </span>

            </div>)
        }
    </div>
}
export default Users;
