import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import Coment from './coment';
import {connect} from "react-redux";
import {UpdateNewMassegeBodycreator, SendMessagecreator} from '../../redux/state/DialogsData_reducer';



let DispatchToProps = (dispatch) =>{
    return{
        UpdateNewMassegeBody: (body) =>{
            dispatch(UpdateNewMassegeBodycreator(body));
        },
        SendMessage: () =>{
            dispatch(SendMessagecreator());
        }
    }
}
let mapStateToProps = (state) => {
    return {
        DialogsData: state.DialogsData,
        isAuth: state.AuthData.isAuth
    }
}
const Comentcontainer = connect(mapStateToProps, DispatchToProps)(Coment);

export default Comentcontainer;
