let initialstate = {
  
}
const DialogsDataReducer = (state, action) =>{
      if (action.type === 'UpdateNewMassegeBody'){
        state.newMessageBody = action.body;
      }
      else if (action.type === 'SendMessage'){
        let newmassege ={
          id: 5,
          message: action.body
        };
        state.Messages.push(newmassege);
        state.newMessageBody = ' ';
    }
    return state;
}
export const SendMessagecreator = (text) =>{
  return{
      type: 'SendMessage', body: text
  }
}
export const UpdateNewMassegeBodycreator = (text) =>{
  return{
      type: 'UpdateNewMassegeBody', body: text
  }
}

export default DialogsDataReducer;
