import React from 'react';
import Profile from './Profile';
import Loader from './loader';
import 'bootstrap/dist/css/bootstrap.min.css';
import classes from './Profile.module.css';
import * as axios from "axios";
import {connect} from 'react-redux';
import {setProfile, setProfilePage} from "../../redux/state/PostsData-reducer";
import {Redirect, withRouter} from "react-router-dom";
class ProfileContainer extends React.Component{
    componentDidMount()  {
        let userId = this.props.match.params.userId;
        if (!userId) {
            userId = 9;
        }
        this.props.setProfilePage(userId);
    }
    render() {

        return <>
            <Profile {...this.props}  profile={this.props.profile}/>
            </>
    }
}
let mapStateToProps = (state) =>({
    profile: state.PostsData.profile,
    isAuth: state.AuthData.isAuth
});
let AuthReedirectComponent = (props) =>{
    if(this.props.isAuth===false){
        return <Redirect to='/login'/>
    }
    return <Dialogs {...props} />
}
let WithUrlDataContainerComponent = withRouter(AuthReedirectComponent);
export default connect(mapStateToProps, {setProfilePage}) (WithUrlDataContainerComponent);
