import classes from "./Users.module.css";
import React from "react";

const Pagination = ({curentPage, onPageChanged, totalItemsCount, pageSize, portonsize = 10}) =>{
    let pagesCount = Math.ceil(totalItemsCount / pageSize);
    let pages = [];
    for (let i = 1; i <= 20; i++) {
        pages.push(i);
    }
    let portionCount = Math.ceil(pagesCount / portionSize);
    let [portionNumber, setPortionNumber] = useState(1);
    let leftPortionPageNumber = (portionNumber - 1) * portionSize + 1;
    let rightPortionPageNumber = portionNumber * portionSize;
    return <div className={classes.user_card_wrap}>
        <div className={classes.spans}>
            {pages.map(p => {
                return <span className={curentPage === p && classes.SelectedPage}
                             onClick={() => {
                                 onPageChanged(p);
                             }}> {p} </span>
            })}
        </div>
    </div>
}
export default Pagination;
