import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import classes from './coment.module.css';
import { Container, Row, Col, Nav, Button, Navbar, Form, FormControl} from 'react-bootstrap';
import Dialogs from './dialogs/dialogs';
import Message from "./messages/messages";

const Coment = (props) => {
let DialogsElement = props.Dialogs.map( d => <Dialogs id={d.id} name={d.name} /> );
let messagesElements = props.Messages.map( m => <Message message={m.message}/> );
return (
    <Container>
        <Row>
        <Col>{DialogsElement}</Col>
        <Col>{messagesElements}
        <div>
                    <textarea  onChange={ OnPostchange  }  ref={newPostElemet} value={props.newMessageBody}> 
                    </textarea>
                </div>
                <div>
                    <button  onClick={ addPost }>Add post</button>
        </div>
        </Col>
        </Row>
    </Container>
    );
}
export default Coment;
