import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import classes from './Users.module.css';
import * as axios from "axios";
import {connect} from "react-redux";

class Users extends React.Component{


        componentDidMount(){
            axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${this.props.currentPage}&cout=${this.props.pageSize}`).then(response => {
                this.props.setuser(response.data.items)
            });
    }
    render() {
            let pagesCount = Math.ceil (this.props.totalUsersCount / this.props.pageSize);
            let pages = [];
            for(let i=1; i<=pagesCount; i++){
                pages.push(i);
            }
        return <div>
            <div>
                {pages.map( p => {
                    return  <span className= {this.props.curentPage === p && classes.SelectedPage} onClick={()=>{}}>{p} </span>
                })}
            </div>
            {
                this.props.users.map(u => <div key={u.id}>
            <span>
                <div>
                    <img className={classes.profil_photo} src={u.photos.small != null ? u.photos.small : "https://pbs.twimg.com/profile_images/1081531670656962561/J9QiHhLT_400x400.jpg"}/>
                </div>
                <div>
                {u.follow
                    ? <button onClick={() => {
                        this.props.unfallow(u.id)
                    }}>Follow</button>
                    : <button onClick={() => {
                        this.props.fallow(u.id)
                    }}>UnFollow</button>
                }
                </div>
            </span>
                    <span>
                <span>
                     <div>{u.name}</div>
                </span>
                <span>
                     <div>
                     {"u.location.city"}
                     </div>
                     <div>
                     {"u.location.country"}
                     </div>
                </span>
            </span>
                </div>)
            }
        </div>
    };
}
export default Users;
