import * as axios from "axios";
const request = "https://social-network.samuraijs.com/api/1.0/users?page=";
export const getUsers = (currentPage =1, pageSize = 10) => {
    return axios.get( request + `${currentPage}&cout=${pageSize}`,
        {
            withCredentials: true
        }, {}).then(response => response.data);
}

export const getUsers2 = (p =1, pageSize = 10) => {
return axios.get(request + `${p}&cout=${pageSize}`,
    {
        withCredentials: true}).then(response => response.data);
}
