import classes from "./Users.module.css";
import React from "react";

const Pagination = ({curentPage, onPageChanged, totalItemsCount, pageSize}) =>{
    let pages = [];
    for (let i = 1; i <= 20; i++) {
        pages.push(i);
    }
    return <div className={classes.user_card_wrap}>
        <div className={classes.spans}>
            {pages.map(p => {
                return <span className={curentPage === p && classes.SelectedPage}
                             onClick={() => {
                                 onPageChanged(p);
                             }}> {p} </span>
            })}
        </div>
    </div>
}
export default Pagination;
