import classes from "./Users.module.css";
import {NavLink} from "react-router-dom";
import React from "react";
let User = ({user, followingInProgress, unfollow, follow}) => {

    return <div className={classes.user_card_wrap}>

            <span>
                <div>
                <div className={classes.user_card}>
                    <NavLink to={'/profile/' + user.id}>
                        <img className={classes.profil_photo} src={user.photos.small != null ? user.photos.small : "https://pbs.twimg.com/profile_images/1081531670656962561/J9QiHhLT_400x400.jpg"}/>
                    </NavLink>

                </div>
                <div>

                {user.followed
                    ? <button disabled={followingInProgress.some(id => id === props.u.id)} onClick={() => {
                        unfollow(user.id);
                    }}>Unfollow</button>
                    : <button disabled={followingInProgress.some(id => id === props.u.id)} onClick={() => {
                        follow(user.id);
                    }}>Follow</button>
                }
                    <span>
                <span>
                     <div>{props.u.name}</div>
                </span>
                <span>
                     <div>
                     {"u.location.city"}
                     </div>
                     <div>
                     {"u.location.country"}
                     </div>
                </span>
            </span>
                </div>
                </div>
            </span>

            </div>
        }

export default User;
