          {id: 4, message: 'Dada', likesCount: 11} 
        ],
          newPostText : '' 
        }
},

dispatch(action){
if (action.type === 'UpdatePost'){
  this._state.PostsData.newPostText = action.Newtext;
  this._RenderFunc(this._state);  
}
else if (action.type === 'AddPost'){
  let newPost ={
    id: 5,
    message: action.postMassage,
    likesCount: 0
  };
  this._state.PostsData.Posts.push(newPost);
  this._state.PostsData.newPostText = ' ';
  this._RenderFunc(this._state);
}
else if (action.type === 'UpdateNewMassegeBody'){
  this._state.DialogsData.newMessageBody = action.body;
  this._RenderFunc(this._state);  
}
else if (action.type === 'SendMessage'){
  let newmassege ={
    id: 6,
    message: action.postMassage,
  };
  this._state.DialogsData.Messages.push(newmassege);
  this._state.DialogsData.newMessageBody = ' ';
  this._RenderFunc(this._state);
}
},
getState(){
  return this._state;
},
_RenderFunc(){
},
