let initialstate = {
  users: [
    { id: 1, photourl:'', name: 'Artem', message: 'Hi, how are you?', follow: true, location: { city: 'Luvetpol', country: 'UK' } },
    { id: 2, name: 'Artem', message: 'Looking foe a job ?', follow: false, location: { city: 'London', country: 'UK' } },
    { id: 3, name: 'Artem', message: 'Я СДАЛ СЕССИЮ', follow: true, location: { city: 'ОДЕССА', country: 'UKRAIN' } },
    { id: 4, name: 'Artem', message: 'Насвай надо ?', follow: false, location: { city: 'АУЛ', country: 'КАЗАКСТАН' } }
  ]
};
const UsersReducer = (state = initialstate, action) => {

  if (action.type === 'fallow') {
    return {
      ...state,
      users: state.users.map(u => {
        if (u.id === action.userID) {
          return {
            ...u, fallow: true
          }
        }
        return u;
      })
    }
  }
  else if (action.type === 'unfallow') {
    return {
      ...state,
      users: state.users.map(u => {
        if (u.id === action.userID) {
          return {
            ...u, follow: false
          }
        }
        return u;
      })
    }
  }
  else if (action.type === 'set_user') {
    return {
      ...state, users: [...state.users, ...action.users]
    }
  }

  else {
    return state;
  }
};
  export const fallowAC = (userID) => {
    return {
      type: 'fallow',
      userID
    }
  }

  export const unfallowAC = (userID) => {
    return {
      type: 'unfallow',
      userID
    }
  }
  export const set_user = (users) => ({type: 'set_user', users})

  export default UsersReducer;
