import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import classes from './Users.module.css';

const Users = (props) => {
    if (props.users.length === 0) {

        axios.get("https://social-network.samuraijs.com/api/1.0/users").then(response =>{
            props.setuser()
        });



    }

    return <div>
        {
            props.users.map(u => <div key={u.id}>
            <span>
                <div>
                    <img className={classes.profil_photo} src={u.photourl}/> 
                </div>
                <div>
                {u.follow
                    ? <button onClick={() => {
                        props.unfallow(u.id)
                    }}>Follow</button>
                    : <button onClick={() => {
                        props.fallow(u.id)
                    }}>UnFollow</button>
                }
                </div>
            </span>
                <span>
                <span>
                     <div>{u.name}</div>
                </span>
                <span>
                     <div>
                     {u.location.city}
                     </div>
                     <div>
                     {u.location.country}
                     </div>
                </span>
            </span>
            </div>)
        }
    </div>
};

export default Users;
