import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import classes from './coment.module.css';
import { Container, Row, Col, Nav, Button, Navbar, Form, FormControl} from 'react-bootstrap';
import Dialogs from './dialogs/dialogs';
import Message from "./messages/messages";
import {Redirect} from "react-router-dom";
import MassageForm_component from "./messages/massage_form";
import {Field, reduxForm} from "redux-form";



const Coment = (props) => {

    let state = props.DialogsData;


    let SendMessage = (massage) =>{
        props.SendMessage(massage);
    }

    let newPostElemet = React.createRef();
    let DialogsElement = state.Dialogs.map( d => <Dialogs id={d.id} name={d.name} /> );
    let messagesElements = state.Messages.map( m => <Message message={m.message}/> );
    let newMessageBody = state.newMessageBody;

return (
    <Container>
        <Row>
        <Col>{DialogsElement}</Col>
        <Col>{messagesElements}
        <div>
            <MassageFormredux onSubmit={SendMessage}/>
              </div>
                <div>
        </div>
        </Col>
        </Row>
    </Container>
    );

    const MassageForm = (props) => {
        return (
            <form onSubmit={props.handleSubmit}>
                <div>
                    <Field placeholder={"Type a massage"} name={"massage"} component={"textarea"}/>
                </div>
                <div>
                    <button onClick={props.onSubmit()}>Add massage</button>
                </div>
            </form>
        )
    }
    const MassageFormredux =  reduxForm({form: 'massage_form'})(MassageForm);

}

export default Coment;
