let initialstate = {
  users: [
   ]
};
const UsersReducer = (state = initialstate, action) => {
  if (action.type === 'fallow') {
    return {
      ...state,
      users: state.users.map(u => {
        if (u.id === action.userID) {
          return {
            ...u, follow: true
          }
        }
        return u;
      })
    }
  }
  else if (action.type === 'unfallow') {
    return {
      ...state,
      users: state.users.map(u => {
        if (u.id === action.userID) {
          return {
            ...u, follow: false
          }
        }
        return u;
      })
    }
  }
  else if (action.type === 'set_user') {
    return {
      ...state, users: [...state.users, ...action.users]
    }
  }

  else {
    return state;
  }
};
  export const fallowAC = (userID) => {
    return {
      type: 'fallow',
      userID
    }
  }

  export const unfallowAC = (userID) => {
    return {
      type: 'unfallow',
      userID
    }
  }

  export const set_user = (users) => ({type: 'set_user', users})

  export default UsersReducer;
