import {UserAPI as API} from "../../components/users/Api";
import {setProfile} from "./PostsData-reducer";

let initialstate = {
    id: null,
    email: null,
    login: null,
    isAuth: false
}
const Auth_reducer = (state = initialstate, action) =>{

    if (action.type === 'Get_user_data'){
      return{
        ...state,
          ...action.data,
          isAuth: true
      }

      }
      else {
        return state;
      }
}

export const Setuserdata = (id, email, login) =>{
  return{
      type: 'Get_user_data', data: {id, email, login}
  }
}
export const setProfilePage =(userId)=> (dispatch) => {
    API.getProfile(userId).then(response => {
        dispatch(setProfile(response.data));
    });
}
export default Auth_reducer;
