import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import icon from '../../../img/icon.png'
import { Container, Row, Col, Nav, Button, Navbar, Form, FormControl, Carousel} from 'react-bootstrap';
import classes from './Posts.module.css';
import Post from './post/post'
import { OnPostchangecreator, addPostcreator } from '../../../redux/state/state';

const Posts = (props) => {
    let postsElements =
    props.PostsData.Posts.map( p => <Post message={p.message} likesCount={p.likesCount}/>);
    let newPostElemet = React.createRef();

    let addPost = () =>{
        let text = newPostElemet.current.value;
        props.dispatch(addPostcreator(text));
    }
    let OnPostchange = () =>{
        let text = newPostElemet.current.value;
        props.dispatch(OnPostchangecreator(text));
    } 
    return (
        <div className={classes.posts}>
                <h3>My posts</h3>
            <div>
                <div>
                    <textarea  onChange={ OnPostchange  }  ref={newPostElemet} value={props.PostsData.newPostText}> 
                    </textarea>
                </div>
                <div>
                    <button  onClick={ addPost }>Add post</button>
                </div>
            </div>
            <div className={classes.posts}>
                { postsElements }
            </div>
        </div>
    );
}
export default Posts;
