let initialstate = {
    Users: [
      {id: 1, name: 'Artem', message: 'Hi, how are you?', follow: true, location:{city: 'Luvetpol', country: 'UK'} },
      {id: 2, name: 'Artem', message: 'Looking foe a job ?', follow: false, location:{city: 'London', country: 'UK'} },
      {id: 3, name: 'Artem', message: 'Я СДАЛ СЕССИЮ', follow: true, location:{city: 'ОДЕССА', country: 'UKRAIN'} },
      {id: 4, name: 'Artem', message: 'Насвай надо ?', follow: false, location:{city: 'АУЛ', country: 'КАЗАКСТАН'} }
    ]
}
const UsersReducer = (state = initialstate, action) =>{
  
    if (action.type === 'UpdatePost'){
      return{
        ...state, newPostText : action.Newtext
      }
      
      }



      else if (action.type === 'AddPost'){
        let newPost = {
          id: 5,
          message: state.newPostText,
          likesCount: 0
        };

        return{
          ...state,
          Posts: [...state.Posts, newPost],
          newPostText : ' '
        };
      }
      else {
        return state;
      }
}

export const fallowAC = () =>{
  return{
      type: 'AddPost'
  }
}

export const OnPostcha = (text) =>{
  return{
      type: 'UpdatePost', Newtext: text
  }
}
export default UsersReducer;
