        users: state.users.map(u=>{
          if(u.id===action.userID){
            return{
              ...u, fallow: true
            }
          }
        }) 
      }
      
      }
      else if (action.type === 'unfallow'){
        let newPost = {
          id: 5,
          message: state.newPostText,
          likesCount: 0
        };

        return{
          ...state,
          Posts: [...state.Posts, newPost],
          newPostText : ' '
        };
      }
      else {
        return state;
      }
}

export const fallowAC = (userID) =>{
