import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import classes from './Users.module.css';
import {NavLink} from "react-router-dom";
import * as axios from "axios";

const Users = (props) => {
    let pagesCount = Math.ceil(props.totalUsersCount / props.pageSize);
    let pages = [];
    for (let i = 1; i <= 20; i++) {
        pages.push(i);
    }
    return <div className={classes.user_card_wrap}>
        <div className={classes.spans}>
            {pages.map(p => {
                return <span className={props.curentPage === p && classes.SelectedPage}
                             onClick={() => {
                                 props.onPageChanged(p);
                             }}> {p} </span>
            })}
        </div>
        {
            props.users.map(u => <div key={u.id}>
            <span>
                <div>
                <div className={classes.user_card}>
                    <NavLink to={'/profile/' + u.id}>
                                            <img className={classes.profil_photo}
                                                 src={u.photos.small != null ? u.photos.small : "https://pbs.twimg.com/profile_images/1081531670656962561/J9QiHhLT_400x400.jpg"}/>
                    </NavLink>

                </div>
                <div>
                {u.follow
                    ? <button onClick={() => {
                        axios.post(`https://social-network.samuraijs.com/api/1.0/follow/${u.id}`, {}, {

                        })
                            .then(response => {
                                this.props.toggleisFetcing(false);
                                this.props.setuser(response.data.items);
                            });
                        props.unfallow(u.id);

                    }}>Follow</button>

                    : <button onClick={() => {props.fallow(u.id);

                    }}>UnFollow</button>
                }
                    <span>
                <span>
                     <div>{u.name}</div>
                </span>
                <span>
                     <div>
                     {"u.location.city"}
                     </div>
                     <div>
                     {"u.location.country"}
                     </div>
                </span>
            </span>
                </div>
                </div>
            </span>

            </div>)
        }
    </div>
}
export default Users;
