let initialstate = {
    id: null,
    email: null,
    login: null,
    isAuth: false
}
const Auth_reducer = (state = initialstate, action) =>{

    if (action.type === 'Get_user_data'){
      return{
        ...state,
          ...action.data
      }

      }
      else {
        return state;
      }
}

export const Setuserdata = (id, email, login) =>{
  return{
      type: 'Get_user_data', data: {id, email, login}
  }
}

export default Auth_reducer;
