let initialstate = {
  Dialogs:
  [
    {id: 1, name: 'Artem'},
    {id: 2, name: 'Artem2'},
    {id: 3, name: 'Artem3'},
    {id: 4, name: 'Artem4'}
  ],
  Messages: [
    {id: 1, message: 'Hi'},
    {id: 2, message: 'How is your it-kamasutra?'},
    {id: 3, message: 'Yo'},
    {id: 4, message: 'Yo'},
    {id: 5, message: 'Yo'}
],
newMessageBody : '' 
}
const DialogsDataReducer = (state=initialstate, action) =>{
      if (action.type === 'UpdateNewMassegeBody'){
        state.newMessageBody = action.body;
      }
      else if (action.type === 'SendMessage'){
        let body = state.newMessageBody;
        let newmassege ={
          id: 5,
          message: body
        };
        state.Messages.push(newmassege);
        state.newMessageBody = ' ';
    }
    return state;
}
export const SendMessagecreator = (text) =>{
  return{
      type: 'SendMessage', body: text
  }
}
export const UpdateNewMassegeBodycreator = (text) =>{
  return{
      type: 'UpdateNewMassegeBody', body: text
  }
}

export default DialogsDataReducer;
