import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import classes from './coment.module.css';
import { Container, Row, Col, Nav, Button, Navbar, Form, FormControl} from 'react-bootstrap';
import Dialogs from './dialogs/dialogs';
import Message from "./messages/messages";
import {UpdateNewMassegeBodycreator, SendMessagecreator} from '../../redux/state/DialogsData_reducer';

const Coment = (props) => {

    let store = props.store.getState();

    let UpdateNewMassegeBody = (e) =>{
        let body = e.target.value;
        props.UpdateNewMassegeBody(body);
    }
    
    let SendMessage = () =>{
        let text = newPostElemet.current.value;
        props.dispatch(SendMessagecreator(text));
    }

    let newPostElemet = React.createRef();
    let DialogsElement = props.Dialogs.map( d => <Dialogs id={d.id} name={d.name} /> );
    let messagesElements = props.Messages.map( m => <Message message={m.message}/> );
    let newMessageBody = props.newMessageBody;
return (
    <Container>
        <Row>
        <Col>{DialogsElement}</Col>
        <Col>{messagesElements}
        <div>
                    <textarea  value={newMessageBody} ref={newPostElemet}  onChange={UpdateNewMassegeBody}>
                        
                    </textarea>
                </div>
                <div>
                    <button onClick={SendMessage}>Add post</button>
        </div>
        </Col>
        </Row>
    </Container>
    );
}
export default Coment;
