let initialstate = {
  users: [
    { id: 1, photourl:'https://xochuanime.online/assets/cache_image/assets/img/2019/pyhl2_0x400_919.webp', name: 'Artem', message: 'Hi, how are you?', follow: true, location: { city: 'Luvetpol', country: 'UK' } },
   ]
};
const UsersReducer = (state = initialstate, action) => {
  console.log(state.users);
  if (action.type === 'fallow') {
    return {
      ...state,
      users: state.users.map(u => {
        if (u.id === action.userID) {
          return {
            ...u, follow: true
          }
        }
        return u;
      })
    }
  }
  else if (action.type === 'unfallow') {
    return {
      ...state,
      users: state.users.map(u => {
        if (u.id === action.userID) {
          return {
            ...u, follow: false
          }
        }
        return u;
      })
    }
  }
  else if (action.type === 'set_user') {
    return {
      ...state, users: [...state.users, ...action.users]
    }
  }

  else {
    return state;
  }
};
  export const fallowAC = (userID) => {
    return {
      type: 'fallow',
      userID
    }
  }

  export const unfallowAC = (userID) => {
    return {
      type: 'unfallow',
      userID
    }
  }

  export const set_user = (users) => ({type: 'set_user', users})

  export default UsersReducer;
