import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import classes from './Users.module.css';

const Users = (props) => {
    if (props.users.length === 0) {
        props.setuser( [
                    {
                        id: 1,
                        photourl: 'https://xochuanime.online/assets/cache_image/assets/img/2019/pyhl2_0x400_919.webp',
                        name: 'Artem',
                        message: 'Hi, how are you?',
                        follow: true,
                        location: {city: 'Luvetpol', country: 'UK'}
                    },
                    {
                        id: 2,
                        photourl: 'https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcSEUDk0OvKeIbNfsxWZ9u6TJhOLGI4G31N99Q&usqp=CAU',
                        name: 'Artem',
                        message: 'Looking foe a job ?',
                        follow: false,
                        location: {city: 'London', country: 'UK'}
                    },
                    {
                        id: 3,
                        photourl: 'https://avatars.mds.yandex.net/get-zen_doc/44972/pub_5df04bba34808200af96206d_5df3e2e89515ee00b1ad1b04/scale_1200',
                        name: 'Artem',
                        message: 'Я СДАЛ СЕССИЮ',
                        follow: true,
                        location: {city: 'ОДЕССА', country: 'UKRAIN'}
                    },
                    {
                        id: 4,
                        photourl: 'https://i.pinimg.com/564x/e2/97/e9/e297e98fc19ac2f9a9d1392c3c097fcc.jpg',
                        name: 'Artem',
                        message: 'Насвай надо ?',
                        follow: false,
                        location: {city: 'АУЛ', country: 'КАЗАКСТАН'}
                    }
                ]
        );

    }

    return <div>
        {
            props.users.map(u => <div key={u.id}>
            <span>
                <div>
                    <img className={classes.profil_photo} src={u.photourl}/> 
                </div>
                <div>
                {u.follow
                    ? <button onClick={() => {
                        props.unfallow(u.id)
                    }}>Follow</button>
                    : <button onClick={() => {
                        props.fallow(u.id)
                    }}>UnFollow</button>
                }
                </div>
            </span>
                <span>
                <span>
                     <div>{u.name}</div>
                </span>
                <span>
                     <div>
                     {u.location.city}
                     </div>
                     <div>
                     {u.location.country}
                     </div>
                </span>
            </span>
            </div>)
        }
    </div>
};

export default Users;
