let store = {
dispatch(action){
if (action.type == 'AddPost'){
  let newPost ={
    id: 5,
    message: postMassage,
    likesCount: 0
  };
  this._state.Posts.push(newPost);
  this._RenderFunc(this._state);
} 
else if (action.type == 'UpdatePost'){
  this._state.newPostText = action.Newtext;
  this._RenderFunc(this._state);
}
},
getState(){
  return this._state;
},
_RenderFunc(){
},
subscrybe(observer){
  this._RenderFunc = observer;
}
}
export default store;
